from typing import List, Tuple


def find_closest_elements(numbers: List[float]) -> Tuple[float, float]:
    """
    From a supplied list of numbers (of length at least two) select and return two that are the closest to each
    other and return them in order (smaller number, larger number).

    :param numbers: A list of floats
    :return: A tuple of two floats representing the two closest numbers in the list
    """
    numbers.sort()
    min_diff = float('inf')
    closest_pair = None
    for i in range(len(numbers)-1):
        diff = numbers[i+1] - numbers[i]
        if diff < min_diff:
            min_diff = diff
            closest_pair = (numbers[i], numbers[i+1])
    if closest_pair is None:
        return (numbers[0], numbers[-1])
    else:
        if numbers.index(closest_pair[0]) < numbers.index(closest_pair[1]):
            return closest_pair
        else:
            return (closest_pair[1], closest_pair[0]) if closest_pair is not None else (numbers[0], numbers[-1])

