    prime_fib returns n-th number that is a Fibonacci number and it's also prime.
    """
    def is_prime(num):
        """
        is_prime returns True if the given number is prime, False otherwise.
        """
        if num < 2:
            return False
        for i in range(2, int(num ** 0.5) + 1):
            if num % i == 0:
                return False
        return True

    def is_fib(num):
        """
        is_fib returns True if the given number is a Fibonacci number, False otherwise.
        """
        if num == 0 or num == 1:
            return True
        a, b = 0, 1
        while b < num:
            a, b = b, a + b
        return b == num

    count = 1
    a, b = 1, 2
    while count < n:
        a, b = b, a + b
        if is_prime(b) and is_fib(b):
            count += 1
    return b

