def histogram(test):
    """
    Given a string representing a space separated lowercase letters, return a dictionary
    of the letter with the most repetition and containing the corresponding count.
    If several letters have the same occurrence, return all of them.

    :param test: A string representing a space separated lowercase letters
    :type test: str
    :return: A dictionary of the letter with the most repetition and containing the corresponding count
    :rtype: dict
    """
    if not test:
        return {}

    letters = test.split()
    letter_count = {}

    for letter in letters:
        if letter in letter_count:
            letter_count[letter] += 1
        else:
            letter_count[letter] = 1

    max_count = max(letter_count.values())
    max_letters = [letter for letter, count in letter_count.items() if count == max_count]

