def Strongest_Extension(class_name, extensions):
    strengths = []
    for extension in extensions:
        CAP = sum(1 for c in extension if c.isupper())
        SM = sum(1 for c in extension if c.islower())
        strength = CAP - SM
        strengths.append((strength, extension))
    strengths.sort(reverse=True)
    strongest_strength = strengths[0][0]
    strongest_extensions = [extension for strength, extension in strengths if strength == strongest_strength]
    strongest_extensions.sort()
    strongest = strongest_extensions[0]
    if len(strongest_extensions) > 1:
        for extension in extensions:
            if extension in strongest_extensions:
                return f"{class_name}.{extension}"
    else:
        for extension in extensions:
            if extension == strongest:
                return f"{class_name}.{extension}"
    return f"{class_name}.{strongest}"

